+@OptIn(ExperimentalMaterial3Api::class)
+@Composable
+fun DialogUsuario(
+ usuario: UsuarioEntity?,
+ onDismiss: () -> Unit,
+ onSave: (String, String, String, Boolean, Boolean) -> Unit,
+ carregando: Boolean,
+ erro: String?
+) {
+ var nome by remember { mutableStateOf(TextFieldValue(usuario?.nome ?: "")) }
+ var email by remember { mutableStateOf(TextFieldValue(usuario?.email ?: "")) }
+ var senha by remember { mutableStateOf(TextFieldValue("")) }
+ var ehAdmin by remember { mutableStateOf(usuario?.ehAdmin ?: false) }
+ var ativo by remember { mutableStateOf(usuario?.ativo ?: true) }
+
+ AlertDialog(
+ onDismissRequest = onDismiss,
+ title = {
+ Text(text = if (usuario == null) "Novo Usuário" else "Editar Usuário")
+ },
+ text = {
+ Column {
+ OutlinedTextField(
+ value = nome,
+ onValueChange = { nome = it },
+ label = { Text("Nome") },
+ modifier = Modifier.fillMaxWidth(),
+ singleLine = true,
+ enabled = !carregando
+ )
+
+ Spacer(modifier = Modifier.height(8.dp))
+
+ OutlinedTextField(
+ value = email,
+ onValueChange = { email = it },
+ label = { Text("Email") },
+ modifier = Modifier.fillMaxWidth(),
+ singleLine = true,
+ keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Email),
+ enabled = !carregando
+ )
+
+ if (usuario == null) {
+ Spacer(modifier = Modifier.height(8.dp))
+ OutlinedTextField(
+ value = senha,
+ onValueChange = { senha = it },
+ label = { Text("Senha") },
+ modifier = Modifier.fillMaxWidth(),
+ singleLine = true,
+ visualTransformation = PasswordVisualTransformation(),
+ keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Password),
+ enabled = !carregando
+ )
+ }
+
+ Spacer(modifier = Modifier.height(8.dp))
+
+ Row(
+ modifier = Modifier.fillMaxWidth(),
+ horizontalArrangement = Arrangement.SpaceBetween
+ ) {
+ Row(verticalAlignment = Alignment.CenterVertically) {
+ Checkbox(
+ checked = ehAdmin,
+ onCheckedChange = { ehAdmin = it },
+ enabled = !carregando
+ )
+ Text("Administrador")
+ }
+
+ if (usuario != null) {
+ Row(verticalAlignment = Alignment.CenterVertically) {
+ Checkbox(
+ checked = ativo,
+ onCheckedChange = { ativo = it },
+ enabled = !carregando
+ )
+ Text("Ativo")
+ }
+ }
+ }
+
+ erro?.let {
+ Spacer(modifier = Modifier.height(8.dp))
+ Text(
+ text = it,
+ color = MaterialTheme.colorScheme.error,
+ style = MaterialTheme.typography.bodySmall
+ )
+ }
+ }
+ },
+ confirmButton = {
+ Button(
+ onClick = {
+ onSave(
+ nome.text.trim(),
+ email.text.trim(),
+ senha.text,
+ ehAdmin,
+ ativo
+ )
+ },
+ enabled = !carregando
+ ) {
+ if (carregando) {
+ CircularProgressIndicator(modifier = Modifier.size(16.dp))
+ } else {
+ Text("Salvar")
+ }
+ }
+ },
+ dismissButton = {
+ TextButton(
+ onClick = onDismiss,
+ enabled = !carregando
+ ) {
+ Text("Cancelar")
+ }
+ }
+ )
+}
