+// Função para gerar cartas embaralhadas
+fun gerarCartas(): List<CartaJogo> {
+ val emojis = listOf("", "", "", "", "", "", "", "")
+ val cartasDuplicadas = emojis.flatMapIndexed { index, emoji ->
+ listOf(
+ CartaJogo(index * 2, emoji),
+ CartaJogo(index * 2 + 1, emoji)
+ )
+ }.shuffled()
+ return cartasDuplicadas
+}
