 _uiState.value = _uiState.value.copy(carregando = true, erro = null)

 if (email.isBlank() || senha.isBlank()) {
 _uiState.value = _uiState.value.copy(
 carregando = false,
 erro = "Email e senha são obrigatórios"
 )
 return@launch
 }

