 resultado.fold(
 onSuccess = { usuario ->
 println(" Login realizado - Nome: ${usuario.nome}, Admin: ${usuario.ehAdmin}")
 _uiState.value = _uiState.value.copy(
 carregando = false,
 usuarioLogado = usuario,
 estaLogado = true,
 erro = null
 )
 },
 onFailure = { exception ->
 _uiState.value = _uiState.value.copy(
 carregando = false,
 erro = exception.message ?: "Erro no login"
 )
 }
 )
 }
