 _uiState.value =
 _uiState.value.copy(carregando = true, erro = null, sucessoCadastro = false)

 // Validações
 when {
 nome.isBlank() -> {
 _uiState.value = _uiState.value.copy(
 carregando = false,
 erro = "Nome é obrigatório"
 )
 return@launch
 }

 email.isBlank() || !email.contains("@") -> {
 _uiState.value = _uiState.value.copy(
 carregando = false,
 erro = "Email válido é obrigatório"
 )
 return@launch
 }

