 } catch (e: Exception) {
 println("⚠️ Aviso: Não foi possível salvar no Firebase: ${e.message}")
 // Continuar mesmo se Firebase falhar - dados ficam salvos localmente
 }

 Result.success(novoUsuario)
 } catch (e: Exception) {
 println("❌ Erro no cadastro: ${e.message}")
 Result.failure(e)
 }
